import React, {memo} from 'react';
import styled from 'styled-components';

const PortfolioContainer = styled.div`
    background-color: #333333;
    color:#fff !important;
    width:100%;
    height: 100vh;

    .contentWrap{
        max-width:1200px;
        margin:0 auto;

        .contentInner{
            display: flex;
            flex-wrap: wrap;

            .item{
                width:590px;
                height:400px;
                margin-right:10px;
                margin-bottom:50px;

                &:nth-child(2n){
                    margin-right:0;
                }

                .link{
                    display: block;
                    width:100%;
                    height:350px;

                    &.active{
                        position: relative;
                    }

                    img{
                        width:100%;
                        height: 100%;
                        object-fit: cover;
                    }

                    .black{
                        position: absolute;
                        width:95%;
                        height:90%;
                        top:50%;
                        left:50%;
                        transform: translate(-50%, -50%);
                        background-color: #F9A93190;
                        /* display: none; */
                        padding:30px;

                        h2{
                            font-size:24px;
                        }
                    }
                }

                p{
                    margin-top:10px;
                    font-size:18px;
                }
            }
        }
    }
`;

const Portfolio = memo(() => {
    return (
        <PortfolioContainer>
            <div className='contentWrap'>
                <ul className='contentInner'>
                    <li className='item'>
                        <a href='#' className='link active'>
                            <div className='black'>
                                <h2>빽다방 클론코딩</h2>
                            </div>
                            <img src='/img/sampleImg.jpg' />
                        </a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'>
                            <div className='black'></div>
                            <img src='/img/sampleImg.jpg' />
                        </a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'>
                            <div className='black'></div>
                            <img src='/img/sampleImg.jpg' />
                        </a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'>
                            <div className='black'></div>
                            <img src='/img/sampleImg.jpg' />
                        </a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                </ul>
            </div>
        </PortfolioContainer>
    );
});

export default Portfolio;
