import React, {memo} from 'react';
import styled from 'styled-components';

const PortfolioContainer = styled.div`
    background-color: #333333;
    color:#fff !important;
    width:100%;
    height: 100vh;

    .contentWrap{
        max-width:1200px;
        margin:0 auto;

        .contentInner{
            display: flex;
            flex-wrap: wrap;

            .item{
                width:590px;
                margin-right:10px;
                margin-bottom:50px;

                &:nth-child(2n){
                    margin-right:0;
                }

                &:hover{
                    background-color: #000;
                }

                .link{
                    display: block;
                    width:100%;
                    height:300px;

                    img{
                        object-fit: cover;
                        width:100%;
                        height: 100%;
                    }
                }

                p{
                    margin-top:10px;
                    font-size:18px;
                }
            }
        }
    }
`;

const Portfolio = memo(() => {
    return (
        <PortfolioContainer>
            <div className='contentWrap'>
                <ul className='contentInner'>
                    <li className='item'>
                        <a href='#' className='link'><img src='./img/sampleImg.jpg' /></a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'><img src='./img/sampleImg.jpg' /></a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'><img src='./img/sampleImg.jpg' /></a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                    <li className='item'>
                        <a href='#' className='link'><img src='./img/sampleImg.jpg' /></a>
                        <p>빽다방 클론코딩 [사용언어: Next.js]</p>
                    </li>
                </ul>
            </div>
        </PortfolioContainer>
    );
});

export default Portfolio;
