import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../../dataset';

const PalksStoryContainer = styled.div`
    display: flex;

    .storyWrap{
        width:50%;
        height:300px;

        

        .storyInner{
            background-size:cover;
            background-position:60%;

            display: block;
            width:100%;
            height:100%;

            &.brand{
                background-color: #253f85;
                background-size:300px;
                background-position:calc(100% - 60px) center;
                background-repeat:no-repeat;
            }

            .textWrap{
                padding:60px 80px;

                .title{
                    width:400px;
                    font-size:46px;
                    font-family: 'Montserrat', sans-serif;
                    font-weight: 900;
                }

                p{
                    margin-top:20px;
                    font-size:18px;
                    font-family: 'Noto Sans KR', sans-serif;
                    font-weight: 600;
                }

                .moreBtn{
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    margin-top:20px;
                    width:50px;
                    height:50px;
                    background-color: #fff;
                    border-radius: 50%;
                    box-shadow: 3px 3px 10px #00000020;
                }
            }
        }
    }

`;

const PalksStory = memo(() => {
    const {story} = dataset;
    
  return (
    <PalksStoryContainer>
        {story.map((v, i) =>{
            return(
                <section className='storyWrap' key={i}>
                    {/* 마지막 요소를 구하는 수식 */}
                    {/* {console.log(story.length - 1 === i)}; */}
                    {(story.length - 1 === i) ? (
                        <a href='#' className='storyInner brand' style={{backgroundImage: `url(${v.img})`}}>
                            <div className='textWrap'>
                                <h1 className='title'>{v.title}</h1>
                                <p>{v.text}</p>
                                <a href='#' className='moreBtn'>
                                    <img src='/img/icon/view_icon.png' alt='seeMore Button' />
                                </a>
                            </div>
                        </a>
                        ) : (
                        <a href='#' className='storyInner' style={{backgroundImage: `url(${v.img})`}}>
                            <div className='textWrap'>
                                <h1 className='title'>{v.title}</h1>
                                <p>{v.text}</p>
                                <a href='#' className='moreBtn'>
                                    <img src='/img/icon/view_icon.png' alt='seeMore Button' />
                                </a>
                            </div>
                        </a>
                    )}

                        

                    
                    
                </section>

            );
        })}
