import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../../dataset';

const PalksStoryContainer = styled.div`
    display: flex;
    height:300px;

    .storyInner{
        width:50%;

        .common{
        display: block;
        height:300px;

        .coffeeStoryInner{

            .title{
                color:#071f60;
                font-size:44px;
                font-weight:900;
                font-family: 'Montserrat', sans-serif;
            }

            .moreBtn{
                background-color: #fff;
                width:50px;
                height:50px;
                border-radius:50%;
                margin-top:36px;
                display: flex;
                justify-content: center;
                align-items: center;
                box-shadow: 2px 2px 8px #00000015;

                img{
                    width:18px;
                    height:18px;
                }
            }

            span{
                display: block;
                margin-top:20px;
                font-size:18px;
                font-weight: 600;
                font-family: 'Noto Sans KR';
            }
        }
    }
    }

    

    .coffeeStory{
        background-image:url('/img/main_sec1.jpg');
        background-repeat:no-repeat;
        background-size:cover;
        background-position:60%; 
    }
    .brandStory{
        background-color: #253f85;
        background-image:url('/img/main_sec2.jpg');
        background-repeat:no-repeat;
        background-size:300px;
        background-position:calc(100% - 60px) center;

        .coffeeStoryInner{

            .title{
                color:#ffffff;
            }

            span{
                color:#ffffff;
                font-weight: 500;
                opacity: 0.8;
            }
        }
    }
`;

