import React, {memo} from 'react';
import styled from 'styled-components';

const PalksStoryContainer = styled.div`
    display: flex;
    height:300px;

    .storyInner{
        width:50%;

        .common{
        display: block;
        height:300px;

        .coffeeStoryInner{

            .title{
                color:#071f60;
                font-size:44px;
                font-weight:900;
                font-family: 'Montserrat', sans-serif;
            }

            .moreBtn{
                background-color: #fff;
                width:50px;
                height:50px;
                border-radius:50%;
                margin-top:36px;
                display: flex;
                justify-content: center;
                align-items: center;
                box-shadow: 2px 2px 8px #00000015;

                img{
                    width:18px;
                    height:18px;
                }
            }

            span{
                display: block;
                margin-top:20px;
                font-size:18px;
                font-weight: 600;
                font-family: 'Noto Sans KR';
            }
        }
    }
    }

    

    .coffeeStory{
        background-image:url('/img/main_sec1.jpg');
        background-repeat:no-repeat;
        background-size:cover;
        background-position:60%; 
    }
    .brandStory{
        background-color: #253f85;
        background-image:url('/img/main_sec2.jpg');
        background-repeat:no-repeat;
        background-size:300px;
        background-position:calc(100% - 60px) center;

        .coffeeStoryInner{

            .title{
                color:#ffffff;
            }

            span{
                color:#ffffff;
                font-weight: 500;
                opacity: 0.8;
            }
        }
    }
`;

const PalksStory = memo(() => {
  return (
    <PalksStoryContainer>
        <section className='storyInner'>
            <a href='#' className='coffeeStory common'>
                <div className='coffeeStoryInner'>
                    <h1 className='title'>
                        PAIK’S COFFEE<br />
                        STORY
                    </h1>
                    <span>균형잡힌 바디감으로 긴 여운을 남기는 빽다방 커피를 만나보세요.</span>
                    <a href='#' className='moreBtn'>
                        <img src='/img/icon/view_icon.png' />
                    </a>
                </div>
            </a>
        </section>
        <section className='storyInner'>
            <a href='#' className='brandStory common'>
                <div className='coffeeStoryInner'>
                    <h1 className='title'>
                        PAIK’S BRAND<br />
                        STORY
                    </h1>
                    <span>합리적인 가격으로 만나는 맛있고 든든한 빽다방</span>
                    <a href='#' className='moreBtn'>
                        <img src='/img/icon/view_icon.png' />
                    </a>
                </div>
            </a>
        </section>
      <a href='#'></a>
    </PalksStoryContainer>
  );
});

export default PalksStory;
