import React, {memo} from 'react';
import styled from 'styled-components';

const MenuContainer = styled.div`
    width:100%;
    height:470px;
    overflow: hidden;

    

    .menuWrap{
        width:100%;
        height:100%;

        &.hover{
            .menuInner{
                transform: scale(1.2, 1.2);
            }
    }

        .menuInner{
            display: block;
            width:100%;
            height:100%;
            background-repeat: no-repeat;
            background-size: cover;
            background-position:center center;

            
        }
    }
`;

const Menu = memo(() => {
  return (
    <MenuContainer>
      <section className='menuWrap'>
        <a href='#' className='menuInner' style={{backgroundImage: 'url(/img/menu_sec1.jpg)'}}>
            <div className='textWrap'></div>
        </a>
        <a href='#' className='moreBtn'>
            <img src='/img/icon/view_icon.png' alt='seeMore Button' />
        </a>
      </section>
    </MenuContainer>
  );
});

export default Menu;
