import React, {memo} from 'react';
import styled from 'styled-components';
import MoreBtn from '../../components/MoreBtn';
import dataset from '../../dataset';

const MenuContainer = styled.div`
    display: flex;
    flex-wrap: wrap;

    .menuWrap{
        width:100%;
        height:auto;
        overflow: hidden;

        &.w100{
        width:100%;
        }

        &.w60{
            width:60%;
            .menuInner{
                
                .textWrap{
                    .title{
                        color:#071f60;
                    }
                }
            }

            
        }

        &.w40{
            width:40%;
        }


        &:hover img{
            transform: scale(1.02, 1.02);
            transition: all 1s ease-out;
        }
        

        .menuInner{
            display: block;
            width:100%;
            height:100%;
            position: relative;

            .textWrap{
                max-width: 1200px;
                position: absolute;
                top:14%;
                left:10%;
                z-index: 999999999;

                .title{
                    width:200px;
                    font-size:46px;
                    font-family: 'Montserrat', sans-serif;
                    font-weight: 900;
                    color:#6b4d30;
                }

                .divider{
                    width:40px;
                    height:3px;
                    background-color:#6b4d30;
                    margin:16px 0;
                }

                p{
                    width:230px;
                    line-height: 1.4;
                    margin-top:20px;
                    font-size:18px;
                    font-family: 'Noto Sans KR', sans-serif;
                    font-weight: 600;
                    margin-bottom:110px;
                }
            }

            img{
                object-fit: cover;
                width:100%;
                height:100%;
            }
        }
    }
`;

const Menu = memo(() => {
    const {menu} = dataset;
