import React, {memo} from 'react';
import styled from 'styled-components';

const MenuContainer = styled.div`
    width:100%;
    height:470px;
    overflow: hidden;

    .menuWrap{
        width:100%;
        height:100%;

        &:hover img{
            transform: scale(1.02, 1.02);
            transition: all 1s ease-out;
        }
        

        .menuInner{
            display: block;
            width:100%;
            height:100%;
            position: relative;

            .textWrap{
                max-width: 1200px;
                position: absolute;
                top:20%;
                left:15%;
                z-index: 999999999;

                .title{
                    width:200px;
                    font-size:46px;
                    font-family: 'Montserrat', sans-serif;
                    font-weight: 900;
                    color:#6b4d30;
                }

                .divider{
                    width:40px;
                    height:4px;
                    background-color:#6b4d30;
                }

                p{
                    margin-top:20px;
                    font-size:18px;
                    font-family: 'Noto Sans KR', sans-serif;
                    font-weight: 600;
                }
            }

            img{
                object-fit: cover;
                width:100%;
                height: auto;
            }
        }
    }
`;

const Menu = memo(() => {
  return (
    <MenuContainer>
      <section className='menuWrap'>
        <a href='#' className='menuInner'>
            <div className='textWrap'>
                <h1 className='title'>FRESH COFFEE</h1>
                <div className='divider'></div>
                <p>신선한 뉴크롭 원두를 사용하여 추출한 커피메뉴!</p>
            </div>
            <img src='/img/menu_sec1.jpg' alt='CoffeeMenuBanner' />
        </a>
        <a href='#' className='moreBtn'>
            <img src='/img/icon/view_icon.png' alt='seeMore Button' />
        </a>
      </section>
    </MenuContainer>
  );
});

export default Menu;
