import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../../dataset';

const PalksStoryContainer = styled.div`
    display: flex;
    height:300px;
    
    .storyInner{
        width:50%;
        background-color: #00f;

        .storyWrap{
            display: block;
            width:100%;
            height:100%;
            background-repeat: no-repeat;
        }
    }
`;

const PalksStory = memo(() => {
    const {story} = dataset;
  return (
    <PalksStoryContainer>
        {story.map((v, i) =>{
            return(
                <section className='storyInner' key={i}>
                    {(story.length - 1 === i) && (
                        <a href='#' className='brandStoryWrap common' style={{backgroundImage: `url(${v.img})`}}>
                            <div className='storyInner'>
                                <h1 className='title'>
                                    {v.title}
                                </h1>
                            </div>
                        </a>
                    )}
                    <a href='#' className='coffeeStoryWrap common' style={{backgroundImage: `url(${v.img})`}}>
                        <div className='storyInner'>
                            <h1 className='title'>
                                {v.title}
                            </h1>
                        </div>
                    </a>
                </section>
            );
        })}
        
        {/* <section className='storyInner'>
            <a href='#' className='storyWrap'>
                <div className='coffeeStoryInner'>
                    <h1 className='title'>
                        PAIK’S COFFEE<br />
                        STORY
                    </h1>
                    <span>균형잡힌 바디감으로 긴 여운을 남기는 빽다방 커피를 만나보세요.</span>
                    <a href='#' className='moreBtn'>
                        <img src='/img/icon/view_icon.png' />
                    </a>
                </div>
            </a>
        </section>
        <section className='storyInner'>
            <a href='#' className='storyWrap'>
                <div className='coffeeStoryInner'>
                    <h1 className='title'>
                        PAIK’S BRAND<br />
                        STORY
                    </h1>
                    <span>합리적인 가격으로 만나는 맛있고 든든한 빽다방</span>
                    <a href='#' className='moreBtn'>
                        <img src='/img/icon/view_icon.png' />
                    </a>
                </div>
            </a>
        </section> */}
      <a href='#'></a>
    </PalksStoryContainer>
  );
});

export default PalksStory;
