import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../dataset';

const FooterContainer = styled.div`
    background-color: #f7f7f7;
    height:480px;
    padding:60px 80px;

    .footerInner{
      background-color: #00f5;
      width:100%;
      height:100px;
      display: flex;

      .footerInfo{
        width:50%;
        height: 250px;

        h1{
          font-size:22px;
          font-weight: 900;
        }

        p{
          margin-top:8px;
          font-size:14px;
          color:#909090;

          &:first-of-type{
            color:#1a1a1a;
          }
        }

        span{
          font-size:14px;
          color:#909090;
        }

        .subInfo{
          margin-top:50px;
          display: flex;
          flex-wrap: wrap;

          li{
            font-size:14px;
            margin-bottom:10px;
            color:#909090;
          }
        }

        .emblem{
          margin-top:50px;  
          display: flex;
          align-items: center;

          img{
            width:38px;
            height:38px;
            margin-right:12px;
          }

          &::after{
            content: "";
            border-right:1px solid #cccccc;
            width:1px;
            height:34px;
          }
        }

      }

      .footerMenu{
        background-color: #f60;
        width:50%;

        ul{
          background-color: #0f0;
          display: flex;
          flex-wrap: wrap;
          text-align: right;
          justify-content: right;

          li{
            font-size:14px;
          }
        }

      }
    }
`;

const Footer = memo(() => {
  const { footerInfo, subInfo, footerMenu } = dataset.footer;
  console.log(footerInfo);

  return (
    <FooterContainer>
      <div className='footerInner'>
        <div className='footerInfo'>
          <h1>1670-9600</h1>
          {footerInfo.map(({text}, i) =>{
            return(
              <p key={i}>{text}</p>
            );
          })}
          <ul className='subInfo'>
            {subInfo.item.map((v, i) =>{
              return(
                <li key={i}>{v.text}</li>
              );
            })}
          </ul>
          <span>{subInfo.msg}</span>
          <ul className='emblem'>
            <li><a href='#'><img src='/img/emblem01.png' /></a></li>
            <li><a href='#'><img src='/img/emblem02.png' /></a></li>
            <li><a href='#'><img src='/img/emblem03.png' /></a></li>
          </ul>
        </div>
        <div className='footerMenu'>
          <ul>
            {footerMenu.map((v, i) =>{
              return(
                <li key={i}>{v.menu}</li>
              );
            })}
          </ul>
        </div>
      </div>
    </FooterContainer>
  );
});

export default Footer;
