import React, {memo, useCallback} from 'react';
import styled from 'styled-components';
import {Routes, Route} from 'react-router-dom';

import dataset from '../../dataset';

const weeklyBestWoman = styled.div`
            .itemInner{
            display: flex;
            flex-wrap: wrap;
            margin:auto;
            justify-content: center;

            .item{
                width:385px;
                margin-right:20px; 
                margin-bottom:60px;

                /* background-color: #f645; */

                &:nth-child(4n){
                    margin-right:0;
                }



                .imgWrap{
                    position: relative;

                    .productInfo{
                        width:36px;
                        height:auto;
                        position: absolute;
                        bottom:20px;
                        right:20px;
                        display: none;

                    }

                    &:hover{

                        .productInfo{
                            display: block;
                            animation: move 0.3s;
                            cursor: pointer;
                        }
                        img{
                            opacity:0.8;
                        }  
                    }

                    @keyframes move{
                        0% {
                            bottom:5px;
                        } 100% {
                            bottom:20px;
                        }
                    }

                    &.weeklyBest{
                        &::after{
                            content: "1";
                            position: absolute;
                            font-size:26px;
                            display: flex;
                            align-items: center;
                            justify-content: center;
                            color:#fff;
                            top:0;
                            left:0;
                            width:60px;
                            height:60px;
                            background-color: #e86434;
                        }
                    }

                    

                    img{
                        width:100%;
                    }
                } 
                
                .description{
                    .productName{
                        display: flex;
                        justify-content: space-between;
                        margin-top:14px;

                        .wish{
                            cursor: pointer;
                            background-image:url("/img/icon/wish.png");
                            background-repeat: no-repeat;
                            width:20px;
                            height:20px;
                        }

                        &.active::after{
                            content: "";
                        }

                        .title{
                            display: block;
                            /* background-color: #00f; */
                            text-align: left;
                            height:auto;
                            font-size:17px;
                            width:310px;

                            overflow: hidden;
                            white-space: nowrap;
                            text-overflow: ellipsis;
                            word-break: break-all;
                        }
                    }

                    .price{
                        text-align: left;
                        display: flex;
                        align-items: baseline;
                        margin-top:6px;
                        

                        .nowPrice{
                            font-size:22px;
                            font-weight: 500;
                            margin-right:8px;
                        }

                        .beforePrice{
                            font-size:16px;
                            color:#aaaaaa;
                            text-decoration: line-through;
                            margin-right:8px;
                        }

                        .discountPercent{
                            font-size:18px;
                            color:#c41e20;
                        }
                    }

                    .colorBox{
                        /* background-color: #f0f5; */
                        margin-top:14px;
                        display: flex;

                        .colorChip{
                            display: block;
                            width:14px;
                            height:14px;
                            border:1px solid #00000020;
                            background-color: #fff;
                            margin-right:4px;
                        }
                    }

                    .reviewBox{
                        text-align: left;
                        margin-top:14px;
                        

                        .review{
                            font-size:14px;
                            color:#555;
                        }
                    }
                }
            }
        }
`;

const HoverMenu = styled.div`
    position: absolute;
    text-align: left;
    bottom:-160px;
    right:0;
    width:360px;
    /* height:160px; */
    background-color: #fff;
    /* padding:6px 20px; */
    overflow: auto;
    /* border:1px solid #eeeeee; */
    box-sizing: border-box;
    border:1px solid #eeeeee;
    padding:6px 20px;
    height: 0;
    opacity: 0;

    ul{ 
        box-sizing: border-box;
        height: 100%;
        overflow-y: auto;

        li{
            border-bottom: 1px solid #0003;
            padding:14px 0;
            cursor: pointer;
            font-size:13px;
        }
    }

    &.active {
        opacity: 1;
        height: 160px;
    }
`;

const WeeklyBestWoman = memo(() => {
    const {weeklyBest} = dataset.content;

    const onInfoHover = useCallback((e) =>{
        e.preventDefault();
        const current = e.currentTarget;

        const currentHover = current.parentElement.querySelector('.HoverMenu');
        console.log(currentHover);

        currentHover.classList.toggle('active');
        
    },[]);

    const onWish = useCallback((e) =>{
        e.preventDefault();

        const current = e.currentTarget;

        const heart = current.parentElement.querySelector('.wish');
        console.log(heart);

        heart.classList.toggle('active');

        if(heart.classList.contains("active")){
            heart.style.backgroundImage = "url(/img/icon/wishON.png)"
        } else{
            heart.style.backgroundImage = "url(/img/icon/wish.png)"
        }
 
    }, []);

  return (
    <ul className='itemInner'>
            {weeklyBest.map((v, i) =>{
                return(
                    <li key={v.id} className='item'>
                        <div className='imgWrap weeklyBest'>
                            <a href='#'><img src={v.img} className='productImg' alt='weeklyBest-img' /></a>
                            <img className='productInfo' onClick={onInfoHover} src='/img/icon/hamburger-menu.png' alt='hamburger-menu' />
                            <HoverMenu className='HoverMenu'>
                                <ul className='hoverItemWrap'>
                                    {v.hoverInfo.map((j, k) =>{
                                        return(
                                            <li key={k}>{j.text}</li>
                                        );
                                    })}
                                </ul>
                            </HoverMenu>
                       </div>
                        <div className='description'>
                            <div className='productName'>
                                <span><a href={v.url} className='title'>{v.title}</a></span>
                                <i className='wish' onClick={onWish}></i>
                            </div>
                            <div className='price'>
                                <span className='nowPrice'>{v.nowPrice}</span>
                                <span className='beforePrice'>{v.beforePrice}</span>
                                <span className='discountPercent'>{v.discountPercent}</span>
                            </div>
                            <div className='colorBox'>
                                {v.colorChip.map((j, k) => {
                                return (
                                    <span className='colorChip' key={k} style={{backgroundColor :`${j.hexCode}`}}></span>
                                );
                                })}
                            </div>
                            <div className='reviewBox'>
                                <span className='review'>리뷰{v.review}건</span>
                            </div>
                        </div>
                    </li>
                );
            })}
        </ul>
    );
});

export default WeeklyBestWoman;
