import React, {memo, useCallback} from 'react';
import styled from 'styled-components';
import {Routes, Route} from 'react-router-dom';

import dataset from '../../dataset';

const ContentContainer = styled.div`
    text-align: center;
    margin: 0 auto;
    /* background-color: #ff05; */
    width:100%;

    .mainTitle{
        margin-top:96px;
        font-size:42px;
        font-weight: 700;
        margin-bottom:60px;

        &.newProduct{
            margin-top:90px;
        }
    }

    .menuWrap{
        display: flex;
        justify-content: center;

        .link{
            position: relative;
            margin-right:40px;

            &:last-child{
                margin-right:0;
            }

            a{
                font-size:20px;

                &.active{
                font-weight: 600;
                border-bottom:3px solid #000000;
                padding-bottom:6px;
                
                &::after{
                    content: "";
                    position: absolute;
                    right:-8px;
                    top:-6px;
                    width:8px;
                    height:8px;
                    background-color: #c41e20;
                    border-radius: 50%;
                }
            }
            }
        }
    }

    .itemWrap{
        box-sizing: border-box;
        /* background-color: #f645; */
        margin: 0 auto;
        width:1600px;
        margin-top:50px;
        display: flex;

        .itemInner{
            display: flex;
            flex-wrap: wrap;
            margin:auto;
            justify-content: center;

            .item{
                width:385px;
                margin-right:20px; 
                margin-bottom:60px;

                /* background-color: #f645; */

                &:nth-child(4n){
                    margin-right:0;
                }



                .imgWrap{
                    position: relative;

                    .productInfo{
                        width:36px;
                        height:auto;
                        position: absolute;
                        bottom:20px;
                        right:20px;
                        display: none;

                    }

                    &:hover{

                        .productInfo{
                            display: block;
                            animation: move 0.3s;
                            cursor: pointer;
                        }
                        img{
                            opacity:0.8;
                        }  
                    }

                    @keyframes move{
                        0% {
                            bottom:5px;
                        } 100% {
                            bottom:20px;
                        }
                    }

                    &.weeklyBest{
                        &::after{
                            content: "1";
                            position: absolute;
                            font-size:26px;
                            display: flex;
                            align-items: center;
                            justify-content: center;
                            color:#fff;
                            top:0;
                            left:0;
                            width:60px;
                            height:60px;
                            background-color: #e86434;
                        }
                    }

                    

                    img{
                        width:100%;
                    }
                } 
                
                .description{
                    .productName{
                        display: flex;
                        justify-content: space-between;
                        margin-top:14px;

                        .wish{
                            cursor: pointer;
                            background-image:url("/img/icon/wish.png");
                            background-repeat: no-repeat;
                            width:20px;
                            height:20px;
                        }

                        &.active::after{
                            content: "";
                        }

                        .title{
                            display: block;
                            /* background-color: #00f; */
                            text-align: left;
                            height:auto;
                            font-size:17px;
                            width:310px;

                            overflow: hidden;
                            white-space: nowrap;
                            text-overflow: ellipsis;
                            word-break: break-all;
                        }
                    }

                    .price{
                        text-align: left;
                        display: flex;
                        align-items: baseline;
                        margin-top:6px;
                        

                        .nowPrice{
                            font-size:22px;
                            font-weight: 500;
                            margin-right:8px;
                        }

                        .beforePrice{
                            font-size:16px;
                            color:#aaaaaa;
                            text-decoration: line-through;
                            margin-right:8px;
                        }

                        .discountPercent{
                            font-size:18px;
                            color:#c41e20;
                        }
                    }

                    .colorBox{
                        /* background-color: #f0f5; */
                        margin-top:14px;
                        display: flex;

                        .colorChip{
                            display: block;
                            width:14px;
                            height:14px;
                            border:1px solid #00000020;
                            background-color: #fff;
                            margin-right:4px;
                        }
                    }

                    .reviewBox{
                        text-align: left;
                        margin-top:14px;
                        

                        .review{
                            font-size:14px;
                            color:#555;
                        }
                    }
                }
            }
        }

        
    }

    .banner{
        display: flex;
        width:100%;
        margin: 130px auto 120px;
    }
`;

const WeeklyBestWoman = memo(() => {
    const {weeklyBest} = dataset.content;

    const onInfoHover = useCallback((e) =>{
        e.preventDefault();
        const current = e.currentTarget;

        const currentHover = current.parentElement.querySelector('.HoverMenu');
        console.log(currentHover);

        currentHover.classList.toggle('active');
        
    },[]);

  return (
    <ul className='itemInner'>
            {weeklyBest.map((v, i) =>{
                return(
                    <li key={v.id} className='item'>
                        <div className='imgWrap weeklyBest'>
                            <a href='#'><img src={v.img} className='productImg' alt='weeklyBest-img' /></a>
                            <img className='productInfo' onClick={onInfoHover} src='/img/icon/hamburger-menu.png' alt='hamburger-menu' />
                            <HoverMenu className='HoverMenu'>
                                <ul className='hoverItemWrap'>
                                    {v.hoverInfo.map((j, k) =>{
                                        return(
                                            <li key={k}>{j.text}</li>
                                        );
                                    })}
                                </ul>
                            </HoverMenu>
                       </div>
                        <div className='description'>
                            <div className='productName'>
                                <span><a href={v.url} className='title'>{v.title}</a></span>
                                <i className='wish' onClick={onWish}></i>
                            </div>
                            <div className='price'>
                                <span className='nowPrice'>{v.nowPrice}</span>
                                <span className='beforePrice'>{v.beforePrice}</span>
                                <span className='discountPercent'>{v.discountPercent}</span>
                            </div>
                            <div className='colorBox'>
                                {v.colorChip.map((j, k) => {
                                return (
                                    <span className='colorChip' key={k} style={{backgroundColor :`${j.hexCode}`}}></span>
                                );
                                })}
                            </div>
                            <div className='reviewBox'>
                                <span className='review'>리뷰{v.review}건</span>
                            </div>
                        </div>
                    </li>
                );
            })}
        </ul>
  );
});

export default WeeklyBestWoman;
