import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../dataset';

const FooterContainer = styled.div`
    background-color: #f7f7f7;
    height:480px;
    padding:60px 80px;

    .footerInner{
      background-color: #00f5;
      width:100%;
      height:100px;
      display: flex;

      .footerInfo{
        background-color: #ff0;
        width:50%;

        h1{
          font-size:22px;
          font-weight: 900;
        }

        p{
          margin-top:8px;
          font-size:14px;
          color:#909090;

          &:first-of-type{
            color:#1a1a1a;
          }

        }

        .subInfo{
          margin-top:50px;
          display: flex;
          flex-wrap: wrap;

          li{
            font-size:14px;
            margin-bottom:10px;
            color:#909090;
          }
        }

      }

      .footerMenu{
        background-color: #f60;
        width:50%;

      }
    }
`;

const Footer = memo(() => {
  const { footerInfo, subInfo } = dataset.footer;
  console.log(footerInfo);

  return (
    <FooterContainer>
      <div className='footerInner'>
        <div className='footerInfo'>
          <h1>1670-9600</h1>
          {footerInfo.map(({text}, i) =>{
            return(
              <p key={i}>{text}</p>
            );
          })}
          <ul className='subInfo'>
            {subInfo.item.map((v, i) =>{
              return(
                <li key={i}>{v.text}</li>
              );
            })}
          </ul>
          <span>{subInfo.msg}</span>
        </div>
        <div className='footerMenu'></div>
      </div>
    </FooterContainer>
  );
});

export default Footer;
