import React, {memo} from 'react';
import styled from 'styled-components';
import dataset from '../dataset';

import * as colors from '../style/colors';

const HeaderContainer = styled.div`
    margin-top: 40px;
    width:100%;
    height:80px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding:0 40px;

    &:hover{
        background-color: #fff;

        .navWrap{
          .logoImg{
            /* background-color:${colors.MAINC} !important; */

            &.actve{
              display:block;
            }
          }

          li{
            &:first-child{
              a{
                color:${colors.MAINC} !important;
              }
            }

            a{
              color:#000 !important;
            }
          }
        }
      }

    position: sticky;
    top:0;
    left:0;
    z-index: 9999999999;

    .navContainer{
      width:100%;
      display: flex;
      height:80px;
      margin:0 auto;

      .navInner{
        width:1600px;
        padding:0 40px;
        display: flex;
        align-items: center;
        justify-content: space-between;

        .navWrap{
          display: flex;
          align-items: center;

          .logoImg{
            width:120px;
            height:auto;
            margin-right:60px;
            

            &.nomal{
              position: relative;
            }

            &.active{
              position: absolute;
              top:0;
              left:0;
              /* display: none; */  
            }
          }

          li{
            margin-left:20px;

            &:first-child{
              margin-left:0;

              a{
                color: ${colors.MAINC};
              }
            }

            a{
              font-size:16px;
              font-weight:600;
              color:#fff;
            }
          }
        }

        .iconWrap{
          ul{
            display: flex;
            align-items: center;

            li{
              margin-left:30px;

              &:first-child{
                margin-left:0;
              }
            }
          }
          
        }
      }
    }
`;

const Header = memo(() => {

  const {nav} = dataset.header;

  return (
    <HeaderContainer>
      <nav className='navContainer'>
        <div className='navInner'>
          <div className='navWrap'>
          <a href='#'><img className='logoImg nomal' alt='logoImg' src='img/logo.png'/></a>
            <a href='#'><img className='logoImg active' alt='logoImg' src='img/logo_colors.png'/></a>
              <ul className='navWrap'>
                {nav.map((v, i) =>{
                  return(
                    <li key={i}>
                      <a href='#'>{v.title}</a>
                    </li>
                  )
                })}
              </ul>
          </div>
          <div className='iconWrap'>
            <ul>
              <li><a href='#'><img alt='user' src='img/icon/top_user.png' /></a></li>
              <li><a href='#'><img alt='search' src='img/icon/top_search.png' /></a></li>
              <li><a href='#'><img alt='wish' src='img/icon/top_wish.png' /></a></li>
              <li><a href='#'><img alt='cart' src='img/icon/top_cart.png' /></a></li>
            </ul>
          </div>
        </div>
      </nav>
    </HeaderContainer>
  );
});

export default Header;
