import React, {memo, useCallback} from 'react';
import styled from 'styled-components';
import dataset from '../../dataset';
import {NavLink} from 'react-router-dom';

const ContentContainer = styled.div`
    text-align: center;
    margin: 0 auto;
    /* background-color: #ff05; */
    width:100%;

    .mainTitle{
        margin-top:96px;
        font-size:42px;
        font-weight: 700;
        margin-bottom:60px;

        &.newProduct{
            margin-top:90px;
        }
    }

    .menuWrap{
        display: flex;
        justify-content: center;

        .link{
            position: relative;
            margin-right:40px;

            &.active{
                font-weight: 600;
                border-bottom:3px solid #000000;
                padding-bottom:6px;
                
                &::after{
                    content: "";
                    position: absolute;
                    right:-8px;
                    top:-6px;
                    width:8px;
                    height:8px;
                    background-color: #c41e20;
                    border-radius: 50%;
                }
            }

            &:last-child{
                margin-right:0;
            }

            a{
                font-size:20px;
            }
        }
    }

    .itemWrap{
        box-sizing: border-box;
        /* background-color: #f645; */
        margin: 0 auto;
        width:1600px;
        margin-top:50px;
        display: flex;

        .itemInner{
            display: flex;
            flex-wrap: wrap;
            margin:auto;
            justify-content: center;

            .item{
                width:385px;
                margin-right:20px; 
                margin-bottom:60px;

                /* background-color: #f645; */

                &:nth-child(4n){
                    margin-right:0;
                }



                .imgWrap{
                    position: relative;

                    .productInfo{
                        width:36px;
                        height:auto;
                        position: absolute;
                        bottom:20px;
                        right:20px;
                        display: none;

                    }

                    &:hover{

                        .productInfo{
                            display: block;
                            animation: move 0.3s;
                            cursor: pointer;
                        }
                        img{
                            opacity:0.8;
                        }  
                    }

                    @keyframes move{
                        0% {
                            bottom:5px;
                        } 100% {
                            bottom:20px;
                        }
                    }

                    &.weeklyBest{
                        &::after{
                            content: "1";
                            position: absolute;
                            font-size:26px;
                            display: flex;
                            align-items: center;
                            justify-content: center;
                            color:#fff;
                            top:0;
                            left:0;
                            width:60px;
                            height:60px;
                            background-color: #e86434;
                        }
                    }

                    

                    img{
                        width:100%;
                    }
                } 
                
                .description{
                    .productName{
                        display: flex;
                        justify-content: space-between;
                        margin-top:14px;

                        .wish{
                            cursor: pointer;
                        }

                        .title{
                            display: block;
                            /* background-color: #00f; */
                            text-align: left;
                            height:auto;
                            font-size:17px;
                            width:310px;

                            overflow: hidden;
                            white-space: nowrap;
                            text-overflow: ellipsis;
                            word-break: break-all;
                        }
                    }

                    .price{
                        text-align: left;
                        display: flex;
                        align-items: baseline;
                        margin-top:6px;
                        

                        .nowPrice{
                            font-size:22px;
                            font-weight: 500;
                            margin-right:8px;
                        }

                        .beforePrice{
                            font-size:16px;
                            color:#aaaaaa;
                            text-decoration: line-through;
                            margin-right:8px;
                        }

                        .discountPercent{
                            font-size:18px;
                            color:#c41e20;
                        }
                    }

                    .colorBox{
                        /* background-color: #f0f5; */
                        margin-top:14px;
                        display: flex;

                        .colorChip{
                            display: block;
                            width:14px;
                            height:14px;
                            border:1px solid #00000020;
                            background-color: #fff;
                            margin-right:4px;
                        }
                    }

                    .reviewBox{
                        text-align: left;
                        margin-top:14px;
                        

                        .review{
                            font-size:14px;
                            color:#555;
                        }
                    }
                }
            }
        }

        
    }

    .banner{
        display: flex;
        width:100%;
        margin: 130px auto 120px;
    }
`;

const HoverMenu = styled.div`
    position: absolute;
    text-align: left;
    bottom:-160px;
    right:0;
    width:360px;
    height:160px;
    background-color: #fff;
    padding:6px 20px;
    overflow: auto;
    border:1px solid #bbbbbb;

    ul{ 

        li{
            border-bottom: 1px solid #0003;
            padding:14px 0;
            cursor: pointer;
            font-size:13px;
        }
    }
