import React, {memo, useCallback, useEffect} from 'react';
import {useNavigate, useParams} from 'react-router-dom';
import styled from 'styled-components';

import Title from '../../components/TitleContainer';

import { useSelector, useDispatch } from 'react-redux';
import { getList } from '../../slices/EmpSupContentSlice';

const DetailPagesContainer = styled.div`
    width:100%;
    height:auto;

    .contentWrap{
        width:1200px;
        margin:0 auto;
        color:#333333;

        .summaryWrap{
            margin-top:60px;
            width:100%;

            &:first-child{
                margin-top:140px;
            }

            .title{
                height: auto;
                font-size:24px;
                font-weight: 500;
                margin-bottom:20px;
            }

            .formTable{
                width:100%;
                /* display: table; */

                tbody{
                    border-top:2px solid #333333;

                    tr{
                        height: auto;
                        line-height: 1.3;
                        border-bottom:1px solid #D9D9D9;

                        .row{
                            padding:20px;
                            vertical-align: middle;
                        }

                        td{
                            padding:20px;
                            vertical-align: middle;
                        }

                    }
                }
            }
        }

        .home{
            margin: 60px auto 80px;
            width:auto;
            height:auto;
            padding:13px 60px;
            border:1px solid #34964B;
            border-radius: 2px;
            cursor: pointer;

            display: flex;
            align-items: center;
            justify-content: center;

            color:#34964B;
            font-weight:600;
            background-color: #fff;

        }

        
    }

`;

const DetailPages = memo(() => {
    const navigate = useNavigate();
    const {id} = useParams();

    const onHomeClick = useCallback((e) =>{
        navigate('/empSupport');
    }, []);

    const dispatch = useDispatch();
    const {data, loading, error} = useSelector((state) =>state.EmpSupContentSlice);
    // console.log(data.id);

    useEffect(() =>{
        dispatch(getList());
    }, []);

    return (
        <DetailPagesContainer>
        <Title title='취업지원' txt='취업 준비생 여러분의 취업을 돕기위한 정책들을 확인해보세요.' />
        <div className='contentWrap'>
            <div className='summaryWrap'>
                <h1 className='title'>정책요약</h1>
                {data && data.map((v, i) =>{
                    if(id === v.id){
                        return(
                            <table className='formTable' key={v.id}>
                        <colgroup>
                            <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                            <col style={{width:"38%"}}></col>
                            <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                            <col style={{width:"38%"}}></col>
                        </colgroup>
                        <tbody>
                            <tr>
                                <th className='row'>정책 번호</th>
                                <td>{v.id}</td>
                                <th className='row'>정책 분야</th>
                                <td>{v.type}</td>
                            </tr>
                            <tr>
                                <th className='row'>정책 소개</th>
                                <td colSpan={3}>{v.introduce}</td>
                            </tr>
                            <tr>
                                <th className='row'>지원 내용</th>
                                <td colSpan={3}>
                                    {v.content.map((vv, ii) =>{
                                        return(
                                            <div key ={ii}>
                                                <p>{vv.title}</p>
                                                <p>{vv.subCon1}</p>
                                                <p>{vv.subCon2}</p>
                                                <p>{vv.subCon3}</p>
                                                <p>{vv.subCon4}</p>
                                                <br />
                                            </div>
                                        );
                                    })}
                                </td>
                            </tr>
                            <tr>
                                <th className='row'>지원 규모(명)</th>
                                <td>{v.scale}</td>
                                <th className='row'>비고</th>
                                <td>{v.note}</td>
                            </tr>
                        </tbody>
                    </table>
                        );
                    }
                })}
                
            </div>

            <div className='summaryWrap'>
                <h1 className='title'>신청자격</h1>
                <table className='formTable'>
                    <colgroup>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                    </colgroup>
                    <tbody>
                        <tr>
                            <th className='row'>연령</th>
                            <td>만 15세 ~ 69세</td>
                            <th className='row'>취업 상태</th>
                            <td>미취업자(단, 불완전 취업자는 에외적 가능)</td>
                        </tr>
                        <tr>
                            <th className='row'>학력/전공</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>거주지 및 소득</th>
                            <td colSpan={3}>
                            취업 지원 <br />
                            - 직업 훈련, 일 경험, 취업 알선 제공 <br />
                            - 취업 지원 기간 종료 후 미취업자 대상 *사후관리 지원 <br />
                            *취업처 정보제공, 이력서 클리닉 등 구직기술향상 프로그램 제공, 유선상담 등 <br />

                            <br /> 

                            소득 지원 <br />
                            - 1유형 : 취업 지원 서비스 참여 등 구직활동 의무 이행을 전제로 최저생계 보장을 위한 구직촉진수당(50×6개월) 지급 <br />
                            - 2유형 : 직업 훈련 참여 등 취업 지원 서비스 참여 시 발생하는 취업 활동 비용 일부 지원 <br />
                            - 공통 : 일정 요건이 해당되는 자에게 장기근속 유도를 위해 취업 후 근속기간에 따른 취업 성공 수당 최대 150만 원 지원 <br />
                            (단, 1유형 수급자에 대해서는 조기 취업 유도를 위해 조기 취업 성공 수당 50만 원 1회 추가 지원)
                            </td>
                        </tr>
                        <tr>
                            <th className='row'>사업 운영 기간</th>
                            <td></td>
                            <th className='row'>사업 신청 기간</th>
                            <td>상시</td>
                        </tr>
                        <tr>
                            <th className='row'>참고 사항</th>
                            <td colSpan={3}>국민취업지원제도 참여 요건을 갖추신 분은 취업지원서비스를 제공받으실 수 있고 셍계를 위한 최소한의 소득도 지원받으실 수 있습니다.</td>
                        </tr>
                        <tr>
                            <th className='row'>참여 제한 대상</th>
                            <td colSpan={3}>국민취업지원제도 참여 요건을 갖추신 분은 취업지원서비스를 제공받으실 수 있고 셍계를 위한 최소한의 소득도 지원받으실 수 있습니다.</td>
                        </tr>
                    </tbody>
                </table>
            </div>

            <div className='summaryWrap'>
                <h1 className='title'>신청방법</h1>
                <table className='formTable'>
                    <colgroup>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                    </colgroup>
                    <tbody>
                        <tr>
                            <th className='row'>신청 절차</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>신청 사이트</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>제출 서류</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                    </tbody>
                </table>
            </div>

            <div className='summaryWrap'>
                <h1 className='title'>기타</h1>
                <table className='formTable'>
                    <colgroup>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                        <col style={{width:"12%", backgroundColor:"#f5f5f5"}}></col>
                        <col style={{width:"38%"}}></col>
                    </colgroup>
                    <tbody>
                        <tr>
                            <th className='row'>주관 기관</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>참고사이트 i</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>참고사이트 ii</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                        <tr>
                            <th className='row'>기타</th>
                            <td colSpan={3}>무관</td>
                        </tr>
                    </tbody>
                </table>
            </div>

            <button type='button' className='home' onClick={onHomeClick}>목록</button>
        </div>
        </DetailPagesContainer>
    );
});

export default DetailPages;
