import {createAsyncThunk } from '@reduxjs/toolkit'
import getDefaultSlice from '../helper/ReduxHelper';
import axios from 'axios';

const API_URL = '/empSupportContent';

/** Ajax처리를 위한 미들웨어 함수 정의 */
export const getList = createAsyncThunk("EmpSupContentSlice/getList", async (payload, { rejectWithValue }) => {
    let result = null;

    try {
        // /api/paiksMenu?category=payload.kkk의값
        const response = await axios.get(API_URL);
        result = response.data;
    } catch (err) {
        result = rejectWithValue(err.response);
    }

    return result;
});

export const getItem = createAsyncThunk("EmpSupContentSlice/getItem", async (payload, { rejectWithValue }) => {
    let result = null;

    try {
        // /api/paiksMenu?category=payload.kkk의값
        const response = await axios.get('/empDetail', {
            params: {
                keyword: payload.keyword
            }
        });
        result = response.data;
    } catch (err) {
        result = rejectWithValue(err.response);
    }

    return result;
});

const EmpSupContentSlice = getDefaultSlice("EmpSupContentSlice", [getList, getItem]);

export default EmpSupContentSlice.reducer;
