import React, {memo, useEffect} from 'react';
import styled from 'styled-components';

import mq from '../style/MediaQuery';

import { useSelector, useDispatch } from 'react-redux';
import { getList } from '../slices/HeaderSlice';

const HeaderContainer = styled.div`
    position: static;
    width:100%;
    height:80px;
    background-color: #fff;
    margin: 0 auto;

    .bgHover{
        position: absolute;
        width:100%;
        height:220px;
        top:80px;
        left:0;
        background-color: #f9f9f9;
    }

    .headerWrap{
        max-width: 1200px;
        height: 100%;
        margin:0 auto;
        display: flex;
        justify-content: space-between;
        align-items: center;
        /* background-color: #ff0; */

        

        .navWrap{
            /* background-color: #f0f5; */
            display: flex;
            justify-content: space-between;
            align-items: center;

            .logoWrap{
                width:120px;
                /* background-color: #0f05; */

                .logo{
                    width:100%;
                    height: auto;
                }
            }
            
            .navInner{
                display: flex;
                padding-left:100px;

                .navItem{
                    position: relative;

                    .subMenuWrap{
                        width:200px;
                        position: absolute;
                        left:50%;
                        top:50px;
                        transform: translateX(-50%);
                        text-align: center;
                        /* background-color: #0f05; */

                        .subMenuInner{
                            .subMenuItem{
                                &:first-child{
                                    padding-top:30px;
                                }
                                padding-bottom:10px;
                            }
                        }
                    }

                    .Mainlink{
                        font-size: 16px;
                        line-height: 1.6;
                        font-weight: 500;
                        padding:15px;
                    }
                }
            }
        }

        .infoNavWrap{
            display: flex;

            .infoNavItem{

                &:last-child::before{
                    content:"·";
                    margin:0 5px;
                }
            }
        }
    }
`;

const Header = memo(() => {
    const dispatch = useDispatch();
    const {data, loading, error} = useSelector((state) => state.HeaderSlice);

    useEffect(() =>{
        dispatch(getList());
    }, []);

    return (
        <HeaderContainer>
            {/* {JSON.stringify(data)} */}
        <div className='bgHover'></div>
        <div className='headerWrap'>
            <div className='navWrap'>
                <div className='logoWrap'>
                    <a href='#'><img src='/img/logo.png' className='logo' alt='내일응원로고' /></a>
                </div>
                <ul className='navInner'>
                    {data && data.map((v, i) =>{
                        return(
                            <li className='navItem'>
                                <a href='#' className='Mainlink'>{v.title}</a>
                            </li>
                        );
                    })}
                </ul>
                {/* <ul className='navInner'>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>

                        <div className='subMenuWrap'>
                            <ul className='subMenuInner'>
                                <li className='subMenuItem'>
                                    <a href='#' className='subLink'>국민취업제도</a>
                                </li>
                                <li className='subMenuItem'>
                                    <a href='#' className='subLink'>청년일자리도약장려금</a>
                                </li>
                                <li className='subMenuItem'>
                                    <a href='#' className='subLink'>무료정장대여서비스</a>
                                </li>
                                <li className='subMenuItem'>
                                    <a href='#' className='subLink'>청년상담실</a>
                                </li>
                            </ul>
                        </div>
                    </li>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>
                    </li>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>
                    </li>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>
                    </li>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>
                    </li>
                    <li className='navItem'>
                        <a href='#' className='Mainlink'>취업지원</a>
                    </li>
                </ul> */}
            </div>
            <ul className='infoNavWrap'>
                <li className='infoNavItem'>
                    <a href='#' className='link'>회원가입</a>
                </li>
                <li className='infoNavItem'>
                    <a href='#' className='link'>로그인</a>
                </li>
            </ul>
        </div>
        </HeaderContainer>
    );
});

export default Header;
