import React, {memo, useEffect} from 'react';
import styled from 'styled-components';

import { useSelector, useDispatch } from 'react-redux';
import { getList } from '../../slices/AppliedProjectSlice';

const BannerContentContainer = styled.div`
    width:100%;
    margin-top:60px;
    text-align: center;

    .supContentWrap{
        width:100%;
        height:480px;
        background-color: #f9f9f9;

        .supContentInner{
            max-width: 1200px;
            margin:0 auto;
            padding-top:60px;

            .supConTitle{
                font-size:30px;
                font-weight: 700;
            }

            .supContxt{
                font-size:16px;
                margin-top:10px;
                color:#959595;
            }

            .supContentItemWrap{
                display: flex;
                flex-wrap: wrap;
                margin-top:30px;

                .supContentItem{
                    border-radius: 10px;
                    border:1px solid #d9d9d9;
                    background-color: #fff;
                    height:100px;
                    width:calc(50% - 10px);
                    margin-left:20px;
                    margin-bottom:10px;
                    text-align: left;
                    padding:38px 30px;
                    display: flex;

                    &:nth-child(odd){
                        margin-left:0;
                    }

                    .textWrap{
                        display: flex;
                        align-items: center;

                        .badge{
                            display: flex;
                            align-items: center;
                            justify-content: center;
                            background-color: #fff;
                            border:1px solid #0F7CDF;
                            color:#0F7CDF;
                            border-radius: 2px;
                            margin-right:10px;
                            font-size:14px;
                            width:70px;
                            height: 24px;
                        }

                        .title{
                            height:auto;
                            font-size:18px;
                        }
                    }
                }
            }
        }
    }
`;

const BannerContent = memo(() => {

    const dispatch = useDispatch();
    const {data, loading, error} = useSelector((state) =>state.AppliedProjectSlice);

    useEffect(() =>{
        dispatch(getList());
    }, []);

    return (
        <BannerContentContainer>
            <div className='supContentWrap'>
                <div className='supContentInner'>
                    <h1 className='supConTitle'>신청 중인 지원 사업</h1>
                    <p className='supContxt'>현재 접수 중인 지원 사업에 대해 알아보세요.</p>
                    <div className='supContentItemWrap'>
                        <a href='#' className='supContentItem'>
                            <div className='textWrap'>
                                <span className='badge'>일자리</span>
                                <h4 className='title'>국민취업지원제도</h4>
                            </div>
                        </a>
                        <a href='#' className='supContentItem'>
                            <div className='textWrap'>
                                <span className='badge'>일자리</span>
                                <h4 className='title'>국민취업지원제도</h4>
                            </div>
                        </a>
                        <a href='#' className='supContentItem'>
                            <div className='textWrap'>
                                <span className='badge'>일자리</span>
                                <h4 className='title'>국민취업지원제도</h4>
                            </div>
                        </a>
                        <a href='#' className='supContentItem'>
                            <div className='textWrap'>
                                <span className='badge'>일자리</span>
                                <h4 className='title'>국민취업지원제도</h4>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        </BannerContentContainer>
    );
});

export default BannerContent;
