import React, {memo, useCallback, useRef, useState} from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import ReactQuill from 'react-quill';

import { useSelector, useDispatch } from 'react-redux';
import { postItem } from '../../slices/CommunityListSlice';
import { result } from 'lodash';

import dayjs from 'dayjs';

const ListAddContainer = styled.div`
    background-color:#F9F9F9;
    width:100%;
    height: auto;

    .contentContainer{
        max-width: 1200px;
        height: auto;
        margin:0 auto;
        padding:120px 0 80px;

        .contentWrap{
            .contentForm{

                .title{
                    height: 60px;
                    margin-bottom:20px;
                    font-size:24px;
                    padding:0 20px;
                    border:1px solid #cccccc;

                    &:focus{
                        border:1px solid #d9d9d9;
                    }
                }
                .qlContent{
                    background-color: #ffffff;
                    white-space: pre-wrap;
                }
            }
        }

        .btnWrap{
            margin-top:60px;
            display: flex;
            justify-content: center;

            .btn{
                display: flex;
                width:150px;
                height: 48px;
                align-items: center;
                justify-content: center;
                color:#ffffff;
                border:0;
                background-color:#959595;
                margin-left:10px;
                cursor: pointer;

                &:first-child{margin-left:0;}

                &.edit{background-color:#0F7CDF;}
            }
        }
    }

`;

const ListAdd = memo(() => {
    const [alignState, setAlignState] = useState('');

    const areaRef = useRef();
    const now = dayjs();;
    console.log(now.format('YYYY-MM-DD'));

    const dispatch = useDispatch();
    const navigate = useNavigate();

    // const onAlignLeft = useCallback((e) =>{
    //     e.preventDefault();
    //     const align = areaRef.current;
    //     // console.log(align);
    //     const alignLeft = align.style.textAlign = 'left';
    //     // window.localStorage.setItem("alignLeft", alignLeft);
    //     setAlignState((currentData) => alignLeft);
        
    // }, []);

    // const onAlignCenter = useCallback((e) =>{
    //     e.preventDefault();
    //     const align = areaRef.current;
    //     // console.log(align);
    //     const alignCenter = align.style.textAlign = 'center';
    //     // window.localStorage.setItem("alignCenter", alignCenter);
    //     setAlignState((currentData) => alignCenter);
    // }, []);

    // const onAlignRight = useCallback((e) =>{
    //     e.preventDefault();
    //     const align = areaRef.current;
    //     // console.log(align);
    //     const alignRight = align.style.textAlign = 'right';
    //     window.localStorage.setItem("alignRight", alignRight);
    //     // setAlignState((currentData) => alignRight);
        
    // }, []);

    const handleResizeHeight = useCallback((e) =>{
        const current = areaRef.current;
        current.style.height = 'auto';
        current.style.height = current.scrollHeight + 'px';
    }, [areaRef]);

    /** form태그의 submit버튼이 눌러졌을 때 */
    const onItemSubmit = useCallback((e) =>{
        e.preventDefault();
        const current = e.currentTarget;

        dispatch(postItem({
            title: current.title.value,
            content: current.content.value,
            registration: current.registration.value,
        })).then((result) =>{
            console.log(result);
            navigate(`/communityHome`);
        })
    }, [alignState]);


    return (
        <ListAddContainer>
            <div className='contentContainer'>
                <div className='contentWrap'>
                    <form className='contentForm' onSubmit={onItemSubmit}>
                        <input className='title' placeholder='제목을 입력하세요' name='title' />
                        <ReactQuill className='qlContent' />
                        <div className='btnWrap'>
                            <button type='submit' className='btn edit'>올리기</button>
                        </div>
                    </form>
                    
                </div>
                
                {/* <div className='alignWrap'>
                    <div className='alignBtn left'>
                        <img src='/img/icon/alignLeft.png' />
                    </div>
                    <div className='alignBtn center'>
                        <img src='/img/icon/alignCenter.png' />
                    </div>
                    <div className='alignBtn right'>
                        <img src='/img/icon/alignRight.png' />
                    </div>
                </div>
                <form className='contentWrap' onSubmit={onItemSubmit}>
                    <input type='hidden' name='registration' defaultValue={now.format('YYYY-MM-DD')} />
                    <div className='contentInner'>
                        <input className='title' placeholder='제목을 입력하세요' name='title' />
                        <hr className='divider' />
                        <div className='content' contenteditable="true" ref={areaRef} onChange={handleResizeHeight} />
                    </div>
                    <div className='btnWrap'>
                        <button type='submit' className='btn edit'>올리기</button>
                    </div>
                </form> */}
                
            </div>
        </ListAddContainer>
    );
});

export default ListAdd;
