import React, {memo, useCallback, useRef} from 'react';
import styled from 'styled-components';

const ListAddContainer = styled.div`
    background-color:#F9F9F9;
    width:100%;
    height: auto;

    .contentContainer{
        max-width: 1200px;
        height: auto;
        margin:0 auto;
        padding:120px 0 80px;

        .alignWrap{
            display: flex;
            width:133px;
            height: 50px;
            background-color: #fff;
            margin-bottom: 20px;
            align-items: center;
            padding:0 20px;
            border: 1px solid #D9D9D9;

            .alignBtn{
                width:25px;
                height: auto;
                margin-left:12px;
                cursor: pointer;

                &.left{margin-left:0;}

                img{
                    width:100%;
                }
            }
        }

        .contentWrap{
            width:100%;
            height: max-content;
            padding:33px 40px;
            border-top: 2px solid #333333;
            border: 1px solid #B3B3B3;
            background-color: #fff;

            .title{
                height: 100%;
                font-size:24px;
                border: 0;

                &:focus{
                    outline:none;
                }
            }

            .divider{
                width:100%;
                height: 1px;
                border:0;
                background-color: #B3B3B3;
                margin:32px 0 50px;
            }

            .content{
                border:0;
                width:100%;
                line-height: 1.6;
                text-align: left;

                &:focus{
                    outline:none;
                }
            }


        }

        .btnWrap{
            margin-top:60px;
            display: flex;
            justify-content: center;

            .btn{
                display: flex;
                width:150px;
                height: 48px;
                align-items: center;
                justify-content: center;
                color:#ffffff;
                border:0;
                background-color:#959595;
                margin-left:10px;
                cursor: pointer;

                &:first-child{margin-left:0;}

                &.edit{background-color:#0F7CDF;}
            }
        }
    }

`;

const ListAdd = memo(() => {
    const areaRef = useRef();

    const onAlignLeft = useCallback((e) =>{
        e.preventDefault();
        const align = areaRef.current;
        // console.log(align);

        align.style.textAlign = 'left';
    }, []);

    const onAlignCenter = useCallback((e) =>{
        e.preventDefault();
        const align = areaRef.current;
        // console.log(align);

        align.style.textAlign = 'center';
    }, []);

    const onAlignRight = useCallback((e) =>{
        e.preventDefault();
        const align = areaRef.current;
        // console.log(align);

        align.style.textAlign = 'right';
    }, []);

    const handleResizeHeight = useCallback((e) =>{
        const current = areaRef.current;
        current.style.height = 'auto';
        current.style.height = current.scrollHeight + 'px';
    }, [areaRef]);

    return (
        <ListAddContainer>
            <div className='contentContainer'>
                <div className='alignWrap'>
                    <div className='alignBtn left' onClick={onAlignLeft}>
                        <img src='/img/icon/alignLeft.png' />
                    </div>
                    <div className='alignBtn center' onClick={onAlignCenter}>
                        <img src='/img/icon/alignCenter.png' />
                    </div>
                    <div className='alignBtn right' onClick={onAlignRight}>
                        <img src='/img/icon/alignRight.png' />
                    </div>
                </div>
                <div className='contentWrap'>
                    <input className='title' placeholder='제목을 입력하세요' />
                    <hr className='divider' />
                    <textarea className='content' ref={areaRef} onChange={handleResizeHeight} />
                </div>
                <div className='btnWrap'>
                    <button type='button' data-id="id" className='btn edit'>작성</button>
                    <button type='button' data-id="id" className='btn delete'>삭제</button>
                </div>
            </div>
        </ListAddContainer>
    );
});

export default ListAdd;
