import React, {memo, useCallback, useRef} from 'react';
import styled from 'styled-components';

const ListAddContainer = styled.div`
    background-color:#F9F9F9;
    width:100%;
    height: auto;

    .contentContainer{
        max-width: 1200px;
        height: auto;
        margin:0 auto;
        padding:120px 0 80px;

        .alignWrap{
            display: flex;
            width:133px;
            height: 50px;
            background-color: #fff;
            margin-bottom: 20px;
            align-items: center;
            padding:0 20px;
            border: 1px solid #D9D9D9;

            .alignBtn{
                width:25px;
                height: auto;
                margin-left:12px;
                cursor: pointer;

                &.left{margin-left:0;}

                img{
                    width:100%;
                }
            }
        }

        .contentWrap{
            width:100%;
            height: auto;
            padding:33px 40px;
            border-top: 2px solid #333333;
            border: 1px solid #B3B3B3;
            background-color: #fff;

            .title{
                height: 100%;
                font-size:24px;
            }

            .divider{
                width:100%;
                height: 1px;
                border:0;
                background-color: #B3B3B3;
                margin:32px 0 50px;
            }

            .content{
                margin-bottom:80px;
                line-height: 1.3;
                text-align: left;
            }


        }

        .btnWrap{
            margin-top:60px;
            display: flex;
            justify-content: center;

            .btn{
                display: flex;
                width:150px;
                height: 48px;
                align-items: center;
                justify-content: center;
                color:#ffffff;
                border:0;
                background-color:#959595;
                margin-left:10px;
                cursor: pointer;

                &:first-child{margin-left:0;}

                &.edit{background-color:#0F7CDF;}
            }
        }
    }

`;

const ListAdd = memo(() => {
    const alignContent = useRef();

    const onAlignLeft = useCallback((e) =>{
        e.preventDefault();
        console.log(alignContent.current);

    }, []);

    return (
        <ListAddContainer>
            <div className='contentContainer'>
                <div className='alignWrap'>
                    <div className='alignBtn left' onClick={onAlignLeft}>
                        <img src='/img/icon/alignLeft.png' />
                    </div>
                    <div className='alignBtn center'>
                        <img src='/img/icon/alignCenter.png' />
                    </div>
                    <div className='alignBtn right'>
                        <img src='/img/icon/alignRight.png' />
                    </div>
                </div>
                <div className='contentWrap'>
                    <h1 className='title'>내일배움카드 이거 어떻게 하는 건가요?</h1>
                    <hr className='divider' />
                    <p className='content'>
                    사법권은 법관으로 구성된 법원에 속한다. 대한민국은 국제평화의 유지에 노력하고 침략적 전쟁을 부인한다. 계엄을 선포한 때에는 대통령은 지체없이 국회에 통고하여야 한다.
                    체포·구속·압수 또는 수색을 할 때에는 적법한 절차에 따라 검사의 신청에 의하여 법관이 발부한 영장을 제시하여야 한다. 다만, 현행범인인 경우와 장기 3년 이상의 형에 해당하는 죄를 범하고 도피 또는 증거인멸의 염려가 있을 때에는 사후에 영장을 청구할 수 있다.
                    </p>
                </div>
                <div className='btnWrap'>
                    <button type='button' data-id="id" className='btn edit'>수정</button>
                    <button type='button' data-id="id" className='btn delete'>삭제</button>
                </div>
            </div>
        </ListAddContainer>
    );
});

export default ListAdd;
