import React, {memo} from 'react';
import styled from 'styled-components';
import mq from '../../style/MediaQuery';

import Glider from 'react-glider';

const InfomationContainer = styled.div`
    margin-top:100px;
    width:100%;
    height:100%;
    text-align: center;
    color:#333;
    /* background-color: #ff0; */

    .contentWrap{
        max-width: 1200px;
        margin: 0 auto;

        .bannerWrap{
            border-radius: 10px;
            width:100%;
            height:120px;
            background-color: #ff0;
            background-image: url('./img/MainS-banner.png');
            background-repeat: no-repeat;
            background-position: center center;
            position: relative;

            .textWrap{
                position: absolute;
                width:100%;
                height:100%;
                top:0;
                left:0;
                display: flex;
                align-items: center;
                justify-content: space-between;
                padding:0 50px;

                .textInner{
                    color:#fff;

                    .title{
                        width:100%;
                        height:30px;
                        font-size:26px;
                        font-weight:400;
                        margin-bottom:3px;

                        .point{
                            font-weight: 600;
                            font-size:inherit;
                        }
                    }

                    .txt{
                        width:100%;
                        height:100%;
                    }
                }

                .btn{
                    width:170px;
                    height:44px;
                    background-color: #fff;
                    border-radius:50px;
                    display: flex;
                    justify-content: center;
                    align-items: center;

                    .checkBtn{
                        /* font-size:14px; */
                    }
                }
                
            }
        }

        .customInfoWrap{
            margin-top:100px;

            .infoTitle{
                font-size:30px;
                font-weight: 700;
            }

            .infotxt{
                font-size:16px;
                margin-top:10px;
                color:#959595;
            }
        }
    }

    
`;

const Infomation = memo(() => {
  return (
    <InfomationContainer>
      <div className='contentWrap'>
        <div className='bannerWrap'>
            <div className='textWrap'>
                <div className='textInner'>
                    <h1 className='title'><span className='point'>당신의 취업</span>을 응원합니다.</h1>
                    <p className='txt'>취업 준비생 여러분의 성공적인 취업을 위한 다양한 정책을 확인하세요.</p>
                </div>
                <div className='btn'>
                    <a href='#' className='checkBtn'>취업 정책 확인하기</a>
                </div>
            </div>
        </div>
        <div className='customInfoWrap'>
            <h1 className='infoTitle'>맞춤형 정보</h1>
            <p className='infotxt'>가장 많이 확인하는 지원 정책에 대해 확인해보세요.</p>
            <div className='infoWrap'>
                <Glider
                    className="glider-container"
                    hasArrows
                    slidesToShow={4}
                    slidesToScroll={1}
                    scrollLock
                    iconLeft={<div className='prevButton'><i className="fa-solid fa-chevron-left button"></i></div>}
                    iconRight={<div className='nextButton'><i className="fa-solid fa-chevron-right button"></i></div>}
                    responsive={[
                        {
                            breakpoint: 992,
                            settings: {
                                slidesToShow: "auto",
                                slidesToScroll: "auto",
                                itemWidth: 285,
                                duration: 0.25,
                            },
                        },
                    ]}
                >
                {/* <div className='infoItem'></div>  */}
                {JSON.stringify(data)}
                </Glider>
            </div>
        </div>
      </div>
    </InfomationContainer>
  );
});

export default Infomation;
