import React, {memo} from 'react';
import styled from 'styled-components';
import {NavLink} from 'react-router-dom';

import Title from '../../components/TitleContainer';

const CommunityPageContainer = styled.div`
    color:#333333;

    .contentContainer{
        .contentWrap{
            max-width:1200px;
            height: auto;
            margin:0 auto;

            .searchForm{
                display: flex;
                max-width:1200px;
                height:60px;
                border-radius: 10px;

            }

            .listTable{
                width:100%;
                text-align: center;
                margin-top:30px;

                thead{
                    height: 60px;
                    background-color: #f5f5f5;
                    border-top:2px solid #333333;
                    border-bottom:1px solid #B3B3B3;
                    line-height: 60px;

                    tr{
                        th{
                            font-weight: 600;
                            border-left:1px solid #B3B3B3;

                            &:first-child{
                                border-left:0;
                            }
                        }
                    }
                }

                tbody{
                    tr{
                        height: 60px;
                        border-bottom:1px solid #B3B3B3;
                        line-height: 60px;

                        td{
                            border-left:1px solid #B3B3B3;

                            &:nth-child(2){
                                padding-left:40px;
                                text-align: left;
                            }

                            &:first-child{
                                border-left:0;
                            }

                            .link:active{
                                /* font-weight: 600; */
                                color:#34964B;
                            }
                        }
                    }
                }
            }
        }
    }
`;

const CommunityPage = memo(() => {
    return (
        <CommunityPageContainer>
            <Title title='커뮤니티' txt='여러분의 다양한 의견을 자유롭게 나눠보세요.' />
            <div className='contentContainer'>
                <div className='contentWrap'>
                    <form className='searchForm'>
                        <input type='text' placeholder='제목을 입력하여 검색하세요.' />
                        <button type='button' className='searchBtn'>검색</button>
                    </form>
                    <table className='listTable'>
                        <colgroup>
                            <col style={{width:"8%"}}></col>
                            <col style={{width:"72%"}}></col>
                            <col style={{width:"15%"}}></col>
                        </colgroup>
                        <thead>
                            <tr>
                                <th>번호</th>
                                <th>제목</th>
                                <th>등록일</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>04</td>
                                <td><NavLink to='#' className='link'>디자이너에서 개발자로 틀려고 생각하고 있는데요.</NavLink></td>
                                <td>2024-03-29</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        </CommunityPageContainer> 
    );
});

export default CommunityPage;
