import React, {memo} from 'react';
import styled from 'styled-components';

const InfomationContainer = styled.div`
    margin-top:100px;
    width:100%;
    height:100%;
    /* background-color: #ff0; */

    .bannerWrap{
        max-width: 1200px;
        margin: 0 auto;

        .bannerItem{
            width:100%;
            height:120px;
            background-color: #ff0;
            background-image: url('./img/MainS-banner.png');
            background-repeat: no-repeat;
            background-position: center center;
            position: relative;

            .textWrap{
                position: absolute;
                width:100%;
                height:100%;
                top:0;
                left:0;
                display: flex;
                align-items: center;
                justify-content: space-between;

                .textInner{
                    padding-left:50px;
                    color:#fff;

                    .title{
                        width:100%;
                        height:30px;
                        font-size:26px;
                        font-weight:400;
                        margin-bottom:3px;

                        .point{
                            font-weight: 600;
                            font-size:inherit;
                        }
                    }

                    .txt{
                        width:100%;
                        height:100%;
                    }
                }

                
            }
        }
    }
`;

const Infomation = memo(() => {
  return (
    <InfomationContainer>
      <div className='bannerWrap'>
        <div className='bannerItem'>
            <div className='textWrap'>
                <div className='textInner'>
                    <h1 className='title'><span className='point'>당신의 취업</span>을 응원합니다.</h1>
                    <p className='txt'>취업 준비생 여러분의 성공적인 취업을 위한 다양한 정책을 확인하세요.</p>
                </div>
                <div className='btn'>
                    <a href=''>취업 정책 확인하기</a>
                </div>
            </div>
        </div>
      </div>
    </InfomationContainer>
  );
});

export default Infomation;
