import React, {memo} from 'react';
import styled from 'styled-components';

import { useSelector, useDispatch } from 'react-redux';
import { getList } from '../../slices/CommunityListSlice';

const ListViewContainer = styled.div`
    .contentContainer{
        max-width:1200px;
        margin:120px auto 80px;

        .contentWrap{
            width:100%;
            height:auto;
            

            .contentInner{
                border-top:2px solid #333333;
                border-bottom:1px solid #B3B3B3;

                .title{
                    padding:0 20px;
                    font-size:24px;
                    display: flex;
                    align-items: center;
                    height: 80px;
                    border:0;
                    
                    &:focus{outline:none;}
                }

                .content{
                    padding:50px 20px 80px;
                    width:100%;
                    height: auto;
                    border:0;
                    
                    &:focus{outline:none;}
                }
            }
            
            .btnWrap{
                margin-top:60px;
                display:flex ;
                justify-content: center;
                
                .btn{
                    margin-left:10px;
                    width:150px;
                    height: 48px;
                    border:0;
                    text-align: center;
                    color: #fff;
                    background-color: #959595;

                    &:first-child{margin-left:0;}

                    &.list{background-color:#34964B;}
                    &.edit{background-color:#0F7CDF;}
                }
            }
        }
    }
`;

const ListView = memo(() => {
    const dispatch = useDispatch();
    const {data, loading, error} = useSelector((state) =>state.CommunityListSlice);

    return (
        <ListViewContainer>
            <div className='contentContainer'>
                {data && data.map((v, i) =>{
                        return(
                            <form className='contentWrap' key={i}>
                                <div className='contentInner'>
                                    <input className='title' name='title' defaultValue={v.title} />
                                    <hr className='divider' />
                                    <textarea className='content' name='content' defaultValue={v.content} />
                                </div>
                                <div className='btnWrap'>
                                    <button type='submit' className='btn list'>목록</button>
                                    <button type='submit' className='btn edit'>수정</button>
                                    <button type='submit' className='btn delete'>삭제</button>
                                </div>
                            </form>
                        );
                    })}
                
            </div>
        </ListViewContainer>
    );
});

export default ListView;
